const saveNote = async (data) => {
  try {
    // Save to localStorage first as backup
    localStorage.setItem(`note_${userId}_${Date.now()}`, JSON.stringify(data));

    // Send to n8n webhook
    const response = await fetch(process.env.INGEST_WEBHOOK_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        user_id: userId,
        linkedin_url: data.linkedinUrl,
        note_text: data.noteText
      })
    });

    // Handle response
    if (!response.ok) throw new Error('Failed to save note');

    // Remove from localStorage if successful
    // Or mark as synced
  } catch (error) {
    // Keep in localStorage for retry
    console.error(error);
    throw error;
  }
};
